  external int n_contours;

  @ffi.UnsignedShort()
  external int n_points;

  external ffi.Pointer<FT_Vector> points;

  external ffi.Pointer<ffi.UnsignedChar> tags;

  external ffi.Pointer<ffi.UnsignedShort> contours;

  @ffi.Int()
  external int flags;
}

